import { getBooksService } from "../../getServices/getBooksService";
import { useState, useEffect } from "react";
import axios from "axios";
import { Typography, Image, Carousel, Col, Row, List, Button, Modal, Rate } from 'antd';
import styles from "../../css/BookDetails.module.css"
import { useLocation } from "react-router-dom";

function BookDetails() {
    const [bookDetails, setBookDetails] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [loading, setLoading] = useState(true);
    const { Title, Paragraph } = Typography;
    
    const { search } = useLocation();
    const queryParams = new URLSearchParams(search);
    const authors = queryParams.get("authors").replace(",", " ,");
    const title = queryParams.get("title")
    const workId = queryParams.get("key")      
    
    useEffect(() => {
        const fetchDetails = () => {
            axios.get(`${getBooksService()}/books/get?key=${workId}`).then((response) => {
                if(response.status == 200) {
                    setBookDetails(response.data)
                    setLoading(false)
                }
            });
        }
        fetchDetails()
    }, [workId])

    const showAllSubjects = () => {
        setIsModalOpen(true);
    };

    const handleOk = () => {
        setIsModalOpen(false);
    }

    const handleCancel = () => {
        setIsModalOpen(false);
      };

    return <div className={styles.page}>
        {!loading ?
        <Row>
            <Col span={7}>
                <Carousel className={styles.covers} arrows infinite={false} dots={false}>
                {bookDetails?.covers.map(coverUrl => (
                    <Image
                        height={600}
                        src={coverUrl}
                        fallback="data:image/png;base64,"
                    />
                ))}
                </Carousel>
            </Col>
            <Col span={17} className={styles.bookInfo}>
                <Title style={{ marginBottom: 5 }}>{title}</Title>
                <Title level={3} style={{ marginTop: 0 }}>{authors}</Title>
                {bookDetails?.rating >= 0 ? <Rate className={styles.bookRating} allowHalf disabled defaultValue={bookDetails?.rating}/> : <p>Rating Unavailable</p>}
                <Paragraph className={styles.description}>{bookDetails?.description}</Paragraph>
                <Title level={4}>Subjects / Genres</Title>
                <Row>
                    <Col span={12}>
                    <div className={styles.subjectsContainer}>
                        <List
                            className={styles.subjects}
                            size="large"
                            bordered
                            dataSource={bookDetails?.subjects.slice(0,3)}
                            renderItem={
                                (item) => <List.Item>{item}</List.Item>
                            }
                        />
                    </div>
                    </Col>
                    <Col span={12}>
                        <div className={styles.subjectsContainer}>
                            <List
                                className={styles.subjects}
                                size="large"
                                bordered
                                dataSource={bookDetails?.subjects.slice(3,6)}
                                renderItem={
                                    (item) => <List.Item>{item}</List.Item>
                                }
                            />
                        </div>
                    </Col>
                </Row>
                {bookDetails?.subjects.length > 6 && (
                    <div className={styles.showAllBtn}>
                        <Button onClick={showAllSubjects}>Show all</Button>
                    </div>
                )}
                <Modal title="Subjects / Genres" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                {bookDetails?.subjects.map(subject => (
                    <List.Item>{subject}</List.Item>
                ))}
                </Modal>
            </Col>
        </Row>
        : <p>Loading...</p>}
    </div>
}

export default BookDetails;